The following cope snippet renders a cube. For a proper example, take a look at the documentation or example/example.cpp. #include"Rayex.hpp"

intmain()
{
//Initializetherenderer.
rx::Rayexrenderer;
renderer.init();

//LoadsomegeometryandsubmitittoRAYEX.
autocube=rx::loadObj("models/cube.obj");
renderer.scene().submitGeometry(cube);

//Createaninstanceofthegeometryfromaboveandsubmititforrendering.
autocubeInstance=rx::instance(cube);
renderer.scene().submitGeometryInstance(cubeInstance);

//Themainloop.
while(renderer.isRunning())
{
renderer.run();
}
}
